        if(!this.state.error){
            posts = this.state.posts.map(post => {
                return (
                    // <Link to={'/' + post.id} key={post.id}>
                    <Post title={post.title}
                          key={post.id}
                             author={post.author}
                             showFull={() => this.showFullPostHandler(post.id)}/>
                // </Link>
                )
            })
        }

        return(
            <section className="Posts">
                {posts}
            </section>
        );
    }
}

export default Posts;
